package app.boundary; import javafx.stage.FileChooser; import javafx.stage.Window; import java.io.File; import java.util.Optional; public final class FilePickers { private FilePickers(){} public static Optional<File> open(Window owner,String desc,String...globs){ var fc=new FileChooser(); fc.getExtensionFilters().add(new FileChooser.ExtensionFilter(desc,globs)); return Optional.ofNullable(fc.showOpenDialog(owner)); } }
